INSERT INTO `survey_question` VALUES (4,1,'domanda_1','Quale aminoacido essenziale per la funzione immunitaria è immagazzinato nei muscoli?','domanda_1','',1,-1,'choice',1,'Arginina\r\nGlutammina\r\nLeucina\r\nTriptofano\r\n','Glutammina',10,1,1),(5,1,'domanda_2','Quante porzioni settimanali di salmone si devono assumere per ottenere 1,5 g giornalieri di EPA e DHA?','domanda_2','',1,-1,'choice',1,'2\r\n8\r\n15\r\n20','15',10,1,1),(6,1,'domanda_3','Che cosa rappresenta il carico glicemico?','domanda_3','',1,-1,'choice',1,'L’impatto dei carboidrati totali in un pasto sulla secrezione dell’insulina\r\nLa quantità di carboidrati totali in un pasto\r\nIl numero di calorie in un pasto\r\nLa resistenza insulinica','L’impatto dei carboidrati totali in un pasto sulla secrezione dell’insulina',10,1,1),(7,1,'domanda_4','Qual è il fattore necessario affinché il grasso corporeo in eccesso   cominci a causare un’infiammazione?','domanda_4','',1,-1,'choice',1,'Produzione di proteina C-reattiva\r\nAccumulo di livelli eccessivi di AA\r\nProduzione di citochine infiammatorie\r\nPeso eccessivo sulle articolazioni','Accumulo di livelli eccessivi di AA',10,1,1),(8,1,'domanda_5','Quanti grammi di EPA e DHA apporta un cucchiaio da tavola di olio di fegato  di merluzzo?','domanda_5','',1,-1,'choice',1,'1,25 grammi\r\n5\r\n2,5 grammi\r\n75\r\n','2,5 grammi',10,1,1),(9,1,'domanda_6','Qual è la definizione che il Dottor Sears dà di wellness (benessere)?','domanda_6','',1,-1,'choice',1,'Assenza di infiammazione silente\r\nLivelli insulinici\r\nPercentuale di massa grassa\r\nBuona forma fisica','Assenza di infiammazione silente',10,1,1),(10,1,'domanda_7','Che cos’è il profilo dell’infiammazione silente (SIP)?','domanda_7','',1,-1,'choice',1,'Rapporto tra cortisolo e adrenalina\r\nRapporto tra AA ed EPA\r\nRapporto tra insulina e glucagone\r\nRapporto tra colesterolo totale e colesterolo HDL','Rapporto tra AA ed EPA',10,1,1),(11,1,'domanda_8','Qual è il substrato energetico che determina le nece